    private void mimetodo(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_mimetodo
        // TODO add your handling code here:
                java.text.DecimalFormat fan1=new java.text.DecimalFormat();
        if (jLabel1.getText().equals(a1))
            jLabel1.setText(a2);
        else
            jLabel1.setText(a1);
        if (lastTXT==jTextFieldCent)
        {double cent, fan;
        cent=Double.parseDouble(jTextFieldCent.getText());
        fan=(9*cent)/5+32;

        //fan1.format("##,###.##");
        fan1.applyPattern("##,###.########");
        jTextFieldFarnheit.setText(fan1.format(fan));
        }
        else
        {
        double cent, fan;
        fan=Double.parseDouble(jTextFieldFarnheit.getText());
        cent=(fan-32)*5/9;
        jTextFieldCent.setText(cent+"");
        }
    }//GEN-LAST:event_mimetodo

    private void ultimoTecleado(java.awt.event.FocusEvent evt) {//GEN-FIRST:event_ultimoTecleado
        // TODO add your handling code here:
        lastTXT=(javax.swing.JTextField)(evt.getSource());
        if (lastTXT==jTextFieldCent)
        {jTextFieldFarnheit.setText("");
        }
        else
            jTextFieldCent.setText("");
    }//GEN-LAST:event_ultimoTecleado

